import React from 'react';
import { useLocation } from 'react-router-dom';
import './Results.css'; // Add styling for Results if needed

const Results = () => {
    const location = useLocation();
    const scores = location.state; // Access the passed scores from the Questionnaire component
    const answers = location.state.answers; // Access the user's answers (assuming you also pass them)

    const { stress, anxiety, depression } = scores;

    // Function to analyze the user's answers and derive the final solution
    const getFinalSolutionFromAnswers = () => {
        // Add your logic to analyze the answers and provide appropriate solutions
        const totalYesAnswers = Object.values(answers).filter(answer => answer === true).length;

        if (totalYesAnswers > 6) {
            return "Your answers suggest that you may be experiencing significant levels of stress, anxiety, or depression. It’s important to consult a mental health professional for further guidance.";
        } else if (totalYesAnswers > 3) {
            return "Your answers indicate that you might be dealing with moderate mental health concerns. Engaging in regular relaxation techniques like meditation, exercise, and mindfulness can help. If symptoms persist, consider talking to a therapist.";
        } else {
            return "Based on your answers, you seem to be managing your mental health well. Continue maintaining a healthy lifestyle, staying socially connected, and practicing self-care routines.";
        }
    };

    // Function to get the result message based on score value
    const getResultMessage = (score) => {
        if (score > 5) {
            return "High";
        } else if (score > 2) {
            return "Moderate";
        } else {
            return "Low";
        }
    };

    const stressLevel = getResultMessage(stress);
    const anxietyLevel = getResultMessage(anxiety);
    const depressionLevel = getResultMessage(depression);

    return (
        <div className="results-container">
            <h2>Your Mental Health Results</h2>

            {/* Display stress, anxiety, and depression levels */}
            <p><strong>Stress Level:</strong> {stressLevel}</p>
            <p><strong>Anxiety Level:</strong> {anxietyLevel}</p>
            <p><strong>Depression Level:</strong> {depressionLevel}</p>

            {/* Display final solution based on the user's answers in the questionnaire */}
            <p><strong>Final Solution:</strong> {getFinalSolutionFromAnswers()}</p>

            <p>
                <strong>Note:</strong> If you feel that you need immediate help, please contact a mental health professional.
            </p>
        </div>
    );
};

export default Results;
