import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Results.css';

const Results = () => {
    const [scores, setScores] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const storedScores = localStorage.getItem("results");
        if (storedScores) {
            setScores(JSON.parse(storedScores));
        } else {
            navigate("/"); // Redirect to the questionnaire if no results found
        }
    }, [navigate]);

    if (!scores) {
        return <p>Loading results...</p>;
    }

    const getFinalSuggestion = () => {
        const { stressScore, anxietyScore, depressionScore } = scores;

        // For simplicity, we will assume high if the score is above a certain threshold.
        // You can adjust the thresholds as per your scoring mechanism.
        const highThreshold = 6;
        const moderateThreshold = 3;

        const highCount = [stressScore, anxietyScore, depressionScore].filter(score => score >= highThreshold).length;
        const moderateCount = [stressScore, anxietyScore, depressionScore].filter(score => score >= moderateThreshold && score < highThreshold).length;

        if (highCount >= 2) {
            return "⚠️ High levels of mental health concerns detected. Please consider consulting a professional.";
        } else if (moderateCount >= 2) {
            return "🔔 Moderate concerns noted. Practice self-care and mindfulness. Seek help if needed.";
        } else {
            return "🌟 You're doing well! Keep maintaining your positive habits!";
        }
    };

    return (
        <div className="result-container">
            <h2>Your Mental Health Results</h2>
            <div className="prediction-box">
                <p><strong>Stress Level:</strong> {scores.stressScore}</p>
                <p><strong>Anxiety Level:</strong> {scores.anxietyScore}</p>
                <p><strong>Depression Level:</strong> {scores.depressionScore}</p>
            </div>

            <p><strong>Final Suggestion:</strong> {getFinalSuggestion()}</p>

            <p><strong>Note:</strong> If you're feeling overwhelmed, don't hesitate to reach out for support.</p>

            <button className="retake-btn" onClick={() => navigate("/")}>
                Retake Questionnaire
            </button>
        </div>
    );
};

export default Results;
