import React, { useState, useEffect } from 'react';
import './Journal.css';

const Journal = () => {
    const [entries, setEntries] = useState([]); // State to hold journal entries
    const [entry, setEntry] = useState(''); // State for current entry input

    useEffect(() => {
        // Fetch existing journal entries from the backend
        const fetchEntries = async () => {
            try {
                const response = await fetch('/api/journal');
                const data = await response.json();
                setEntries(data);
            } catch (error) {
                console.error('Error fetching entries:', error);
            }
        };

        fetchEntries();
    }, []);

    const handleEntryChange = (e) => {
        setEntry(e.target.value);
        // Adjust the height of the textarea
        e.target.style.height = 'auto';
        e.target.style.height = `${e.target.scrollHeight}px`;
    };

    const handleAddEntry = async (e) => {
        e.preventDefault(); // Prevent form submission default behavior
        if (entry) {
            try {
                const response = await fetch('/api/journal/add', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ content: entry }),
                });

                if (response.ok) {
                    const newEntry = await response.json();
                    setEntries([...entries, newEntry]); // Add the new entry to the state
                    setEntry(''); // Reset input field
                    alert('Entry added successfully!'); // Success feedback
                } else {
                    alert('Failed to add journal entry'); // Error feedback
                }
            } catch (error) {
                console.error('Error adding entry:', error);
            }
        }
    };

    return (
        <div className="journal-container">
            <center><h2>Journal</h2></center>
            <form onSubmit={handleAddEntry}>
                <textarea
                    value={entry}
                    onChange={handleEntryChange}
                    placeholder="Write about your day or feelings..."
                    required
                ></textarea>
                <button type="submit">Add Entry</button>
            </form>
            <div className="journal-entries">
                <h3>Your Journal Entries:</h3>
                {entries.length === 0 ? (
                    <p>No entries yet.</p>
                ) : (
                    <ul>
                        {entries.map((entry, index) => (
                            <li key={index}>{entry.content}</li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default Journal;
