import React, { useState } from 'react'; // Import React and hooks
import axios from 'axios'; // Import Axios
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import './Questionnaire.css';

const Questionnaire = () => {
    const [answers, setAnswers] = useState({
        mood: '',
        sleep: '',
        energy: '',
        appetite: '',
        interest: '',
        irritability: '',
        concentration: '',
        feelingWorthy: '',
        anxiousThoughts: '',
        panicAttacks: ''
    });

    const navigate = useNavigate(); // Initialize navigate for navigation

    const handleChange = (e) => {
        setAnswers({ ...answers, [e.target.name]: e.target.value });
    };

    const calculateScores = (answers) => {
        let stressScore = getStressScore(answers);
        let anxietyScore = getAnxietyScore(answers);
        let depressionScore = getDepressionScore(answers);

        const stressLevel = stressScore > 5 ? 'High' : stressScore > 2 ? 'Moderate' : 'Low';
        const anxietyLevel = anxietyScore > 5 ? 'High' : anxietyScore > 2 ? 'Moderate' : 'Low';
        const depressionLevel = depressionScore > 5 ? 'High' : depressionScore > 2 ? 'Moderate' : 'Low';

        return {
            stress: stressLevel,
            anxiety: anxietyLevel,
            depression: depressionLevel,
        };
    };

    const getStressScore = (answers) => {
        let score = 0;
        score += (answers.mood === 'Very Low' ? 3 : answers.mood === 'Low' ? 2 : answers.mood === 'Neutral' ? 1 : 0);
        score += (answers.sleep === 'Very Poor' ? 3 : answers.sleep === 'Poor' ? 2 : answers.sleep === 'Neutral' ? 1 : 0);
        return score;
    };

    const getAnxietyScore = (answers) => {
        let score = 0;
        score += (answers.anxiousThoughts === 'Very Often' ? 3 : answers.anxiousThoughts === 'Sometimes' ? 2 : answers.anxiousThoughts === 'Rarely' ? 1 : 0);
        score += (answers.panicAttacks === 'Yes' ? 3 : 0);
        return score;
    };

    const getDepressionScore = (answers) => {
        let score = 0;
        score += (answers.interest === 'Yes, Completely' ? 3 : answers.interest === 'Somewhat' ? 2 : 0);
        score += (answers.feelingWorthy === 'Very Often' ? 3 : answers.feelingWorthy === 'Sometimes' ? 2 : 0);
        return score;
    };

    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent default form submission behavior
        console.log("Submit button clicked!"); // Log for debugging
        
        const scores = calculateScores(answers); // Calculate scores based on answers
        console.log("Scores calculated:", scores); // Log the calculated scores
        
        // Submit user responses and calculated scores to the backend
        try {
            const response = await axios.post('http://localhost:5000/api/questionnaire', {
                answers,
                scores // Send answers and scores to backend
            });
            console.log("Submission successful:", response.data); // Log successful submission
            
            // Redirect to the results page after successful submission, passing the scores
            navigate('/results', { state: scores }); // Pass scores as state
        } catch (error) {
            console.error('Submission failed:', error); // Log error message
        }
    };

    return (
        <div className="questionnaire-container">
            <center><h2>Mental Health Questionnaire</h2></center>
            <form onSubmit={handleSubmit}>
                <div className="input-field">
                    <label>How would you rate your overall mood in the past week?</label>
                    <select name="mood" value={answers.mood} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Very Low">Very Low</option>
                        <option value="Low">Low</option>
                        <option value="Neutral">Neutral</option>
                        <option value="Good">Good</option>
                        <option value="Very Good">Very Good</option>
                    </select>
                </div>

                <div className="input-field">
                    <label>How has your sleep been over the past week?</label>
                    <select name="sleep" value={answers.sleep} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Very Poor">Very Poor</option>
                        <option value="Poor">Poor</option>
                        <option value="Neutral">Neutral</option>
                        <option value="Good">Good</option>
                        <option value="Very Good">Very Good</option>
                    </select>
                </div>

                <div className="input-field">
                    <label>How would you describe your energy levels?</label>
                    <select name="energy" value={answers.energy} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Very Low">Very Low</option>
                        <option value="Low">Low</option>
                        <option value="Moderate">Moderate</option>
                        <option value="High">High</option>
                        <option value="Very High">Very High</option>
                    </select>
                </div>

                <div className="input-field">
                    <label>Have you noticed any changes in your appetite?</label>
                    <select name="appetite" value={answers.appetite} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Increased">Increased</option>
                        <option value="Decreased">Decreased</option>
                        <option value="Normal">Normal</option>
                    </select>
                </div>

                <div className="input-field">
                    <label>Have you lost interest in activities you usually enjoy?</label>
                    <select name="interest" value={answers.interest} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Yes, Completely">Yes, Completely</option>
                        <option value="Somewhat">Somewhat</option>
                        <option value="No, Not at All">No, Not at All</option>
                    </select>
                </div>

                <div className="input-field">
                    <label>How irritable have you felt recently?</label>
                    <select name="irritability" value={answers.irritability} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Very Irritable">Very Irritable</option>
                        <option value="Somewhat Irritable">Somewhat Irritable</option>
                        <option value="Not Irritable">Not Irritable</option>
                    </select>
                </div>

                <div className="input-field">
                    <label>How would you rate your ability to concentrate?</label>
                    <select name="concentration" value={answers.concentration} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Very Poor">Very Poor</option>
                        <option value="Poor">Poor</option>
                        <option value="Neutral">Neutral</option>
                        <option value="Good">Good</option>
                        <option value="Very Good">Very Good</option>
                    </select>
                </div>

                <div className="input-field">
                    <label>How often have you felt unworthy or excessively guilty?</label>
                    <select name="feelingWorthy" value={answers.feelingWorthy} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Very Often">Very Often</option>
                        <option value="Sometimes">Sometimes</option>
                        <option value="Rarely">Rarely</option>
                        <option value="Never">Never</option>
                    </select>
                </div>

                <div className="input-field">
                    <label>How often do you experience anxious thoughts?</label>
                    <select name="anxiousThoughts" value={answers.anxiousThoughts} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Very Often">Very Often</option>
                        <option value="Sometimes">Sometimes</option>
                        <option value="Rarely">Rarely</option>
                        <option value="Never">Never</option>
                    </select>
                </div>

                <div className="input-field">
                    <label>Have you experienced panic attacks?</label>
                    <select name="panicAttacks" value={answers.panicAttacks} onChange={handleChange} required>
                        <option value="">Select</option>
                        <option value="Yes">Yes</option>
                        <option value="No">No</option>
                    </select>
                </div>

                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default Questionnaire;
