import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from '../../../new-mhapp/frontend/src/components/Header';
import Questionnaire from '../../../new-mhapp/frontend/src/components/Questionnaire';
import Results from '../../../new-mhapp/frontend/src/components/Results';
import Journal from '../../../new-mhapp/frontend/src/components/Journal';
import ToDoList from '../../../new-mhapp/frontend/src/components/ToDoList';
import './App.css';

function App() {
    const [results, setResults] = useState(null);

    const handleQuestionnaireSubmit = (answers) => {
        const scores = calculateScores(answers);
        setResults(scores);
    };

    const calculateScores = (answers) => {
        let stressScore = 0;
        let depressionScore = 0;
        let anxietyScore = 0;

        // Calculate stress score based on user responses
        stressScore += (answers.mood === 'Very Low' ? 3 : answers.mood === 'Low' ? 2 : answers.mood === 'Neutral' ? 1 : 0);
        stressScore += (answers.sleep === 'Very Poor' ? 3 : answers.sleep === 'Poor' ? 2 : answers.sleep === 'Neutral' ? 1 : 0);
        stressScore += (answers.energy === 'Very Low' ? 3 : answers.energy === 'Low' ? 2 : answers.energy === 'Moderate' ? 1 : 0);

        // Calculate anxiety score based on user responses
        anxietyScore += (answers.anxiousThoughts === 'Very Often' ? 3 : answers.anxiousThoughts === 'Sometimes' ? 2 : answers.anxiousThoughts === 'Rarely' ? 1 : 0);
        anxietyScore += (answers.panicAttacks === 'Yes' ? 3 : 0);

        // Calculate depression score based on user responses
        depressionScore += (answers.interest === 'Yes, Completely' ? 3 : answers.interest === 'Somewhat' ? 2 : 0);
        depressionScore += (answers.feelingWorthy === 'Very Often' ? 3 : answers.feelingWorthy === 'Sometimes' ? 2 : 0);

        return { stressScore, depressionScore, anxietyScore };
    };

    return (
        <Router>
            <div className="app-container">
                <Header />
                <Routes>
                    <Route
                        path="/questionnaire"
                        element={!results ? <Questionnaire onSubmit={handleQuestionnaireSubmit} /> : <Results scores={results} />}
                    />
                    <Route path="/results" component={<Results />} />
                    <Route path="/journal" element={<Journal />} />
                    <Route path="/todolist" element={<ToDoList />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
