import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Questionnaire.css';

const Questionnaire = () => {
    const [answers, setAnswers] = useState({
        mood: '',
        sleep: '',
        energy: '',
        appetite: '',
        interest: '',
        irritability: '',
        concentration: '',
        feelingWorthy: '',
        anxiousThoughts: '',
        panicAttacks: ''
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setAnswers({ ...answers, [e.target.name]: e.target.value });
    };

    const calculateScores = (answers) => {
        let stressScore = getStressScore(answers);
        let anxietyScore = getAnxietyScore(answers);
        let depressionScore = getDepressionScore(answers);

        return {
            stress: stressScore > 5 ? 'High' : stressScore > 2 ? 'Moderate' : 'Low',
            anxiety: anxietyScore > 5 ? 'High' : anxietyScore > 2 ? 'Moderate' : 'Low',
            depression: depressionScore > 5 ? 'High' : depressionScore > 2 ? 'Moderate' : 'Low',
        };
    };

    const getStressScore = (answers) => {
        let score = 0;
        score += (answers.mood === 'Very Low' ? 3 : answers.mood === 'Low' ? 2 : answers.mood === 'Neutral' ? 1 : 0);
        score += (answers.sleep === 'Very Poor' ? 3 : answers.sleep === 'Poor' ? 2 : answers.sleep === 'Neutral' ? 1 : 0);
        return score;
    };

    const getAnxietyScore = (answers) => {
        let score = 0;
        score += (answers.anxiousThoughts === 'Very Often' ? 3 : answers.anxiousThoughts === 'Sometimes' ? 2 : answers.anxiousThoughts === 'Rarely' ? 1 : 0);
        score += (answers.panicAttacks === 'Yes' ? 3 : 0);
        return score;
    };

    const getDepressionScore = (answers) => {
        let score = 0;
        score += (answers.interest === 'Yes, Completely' ? 3 : answers.interest === 'Somewhat' ? 2 : 0);
        score += (answers.feelingWorthy === 'Very Often' ? 3 : answers.feelingWorthy === 'Sometimes' ? 2 : 0);
        return score;
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
    
        try {
            const response = await axios.post("http://localhost:5000/submit", {
                scores,
            });
    
            const data = response.data;
            localStorage.setItem("result", JSON.stringify(data)); // Store result
            navigate("/results"); // Navigate to results page
        } catch (error) {
            alert("Error submitting data");
            console.error("Submission failed:", error);
        }
    };
    

    return (
        <div className="questionnaire-container">
            <center><h2>Mental Health Questionnaire</h2></center>
            <form onSubmit={handleSubmit}>
                {Object.keys(answers).map((key, index) => (
                    <div key={index} className="input-field">
                        <label>{key.replace(/([A-Z])/g, ' $1').trim()}</label>
                        <select name={key} value={answers[key]} onChange={handleChange} required>
                            <option value="">Select</option>
                            <option value="Very Low">Very Low</option>
                            <option value="Low">Low</option>
                            <option value="Neutral">Neutral</option>
                            <option value="High">High</option>
                            <option value="Very High">Very High</option>
                        </select>
                    </div>
                ))}
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default Questionnaire;
